create table sys_dict_value
(
    id          bigint auto_increment
        primary key,
    scope       varchar(16)  default 'global'                                               not null,
    ident       varchar(32)                                                                 not null,
    label       varchar(64)                                                                 not null,
    val         bigint                                                                      not null,
    order_no    int          default 0                                                      null,
    status      tinyint                                                                     null comment '0=normal,1=ban',
    remark      varchar(128)                                                                null,
    update_by   varchar(32)  default 'admin'                                                null comment 'username',
    pid         bigint       default 0                                                      not null,
    meta        varchar(128) default '{"type":"success","effect":"light","size":"default"}' null comment 'TAG INFO JSON',
    update_time datetime     default CURRENT_TIMESTAMP                                      not null on update CURRENT_TIMESTAMP,
    create_time datetime     default CURRENT_TIMESTAMP                                      not null,
    constraint sys_dict_value_scope_ident_pid_order_no_uindex
        unique (scope, ident, pid, order_no),
    constraint sys_dict_value_scope_ident_val_uindex
        unique (scope, ident, val)
)
    comment '字典值列表';

